import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Jumbotron, Container, Image} from "react-bootstrap";
import styles from "../CSS/Category.module.css";

export default function Threads() {
  const { id } = useParams();
  const [categorys, setCategorys] = useState([]);

  useEffect( () => {
    fetch(`http://localhost:8080/threads/${id}`)
      .then((response) => {
        return response.json()
      })
      .then((data) => {
        setCategorys(data || [])
      })
  }, [id])


  console.log(ColorContext)

  return (
    <Jumbotron id="about" className={styles.Jumbotron} fluid>
      <Container>
        <div className={styles.card}>
          <h1 className={styles.heading}>
            <span className={styles.Us}>Threads</span>
          </h1>
          <div className={styles.links}>
            {categorys.map((category) =>{
              return(
                <div key={category.threadId} className={styles.linkWithImage}>
                  <Image src="https://i.imgur.com/yU6MkEg.png" alt="Logo" style={{ maxHeight: "36px", alignSelf: "center", marginLeft: "10px", paddingBottom: "4px"  }}></Image>
                  <Link className={styles.categoryLink} to={`/category/${category.threadId}`} key={category.threadId}>{category.title} <p>By: {category.authorName}</p></Link>
                </div>
              )
            })}
          </div>
        </div>
      </Container>
      <div className={styles.lineGrade} ></div>
    </Jumbotron>
  );
}
