import React, { useEffect, useState, Fragment } from "react";
import { Link } from "react-router-dom";
import { Jumbotron, Container} from "react-bootstrap";
import styles from "../CSS/Category.module.css";

export default function Category() {
  const [products, setProducts] = useState([]);

  useEffect( () => {
    fetch("http://localhost:8080/categorys/")
      .then((response) => {
        return response.json()
      })
      .then((data) => {
        setProducts(data || [])
      })
  }, [])

  return (
    <Jumbotron id="about" className={styles.Jumbotron} fluid>
      <Container>
        <div className={styles.card}>
          <h1 className={styles.heading}>
            <span className={styles.Us}>Main Category</span>
          </h1>
          {products.map((product) =>{
            return(
              <Fragment key={product.categoryId}>
                <Link className={styles.categoryLink} to={`/category/${product.categoryId}`} key={product.categoryId}>{product.name}</Link>
                <br/>
              </Fragment>
            )
          })}
        </div>
      </Container>
      <div className={styles.lineGrade} ></div>
    </Jumbotron>
  );
}
