import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Jumbotron, Container, Image} from "react-bootstrap";
import styles from "../CSS/Posts.module.css";

export default function Posts() {
  const { id } = useParams();
  const [posts, setPosts] = useState([]);
  const [thread, setThread] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect( () => {
    fetch(`http://localhost:8080/posts/${id}`)
    .then((response) => {
      return response.json()
    })
    .then((data) => {
      setPosts(data || [])

      fetch(`http://localhost:8080/threads/${data[0].threadId}`)
        .then((response) => {
          return response.json()
        })
        .then((data) => {
          setThread(data || [])
        })

        fetch(`http://localhost:8080/users/${data[0].threadId}`)
        .then((response) => {
          return response.json()
        })
        .then((data) => {
          setUsers(data || [])
        })
    })
  }, [id])

  return (
    <Jumbotron id="about" className={styles.Jumbotron} fluid>
      <Container>
        <div className={styles.card}>
          <h1 className={styles.heading}>
            {thread.map((post) =>{
                return(
                  <span key={post.threadId} className={styles.Us}>{post.title}</span>
                )
            })}
          </h1>
            {thread.map((post) =>{
              return(
                <div key={post.threadId} className={styles.cardPost}>
                  <div className={styles.cardPostWithImage}>
                    <Image src="https://www.w3schools.com/howto/img_avatar.png" alt="Logo" className={styles.image}></Image>
                    <p>{post.authorName} <br/></p>
                  </div>
                </div>
              )
            })}

            {posts.map((post, index) =>{
              return(
                <div key={post.postId} className={styles.cardPost}>
                  <div className={styles.cardPostWithImage}>
                    <Image src="https://www.w3schools.com/howto/img_avatar.png" alt="Logo" className={styles.image}></Image>

                    {users[index] !== undefined ? <p>{users[index].avatarName} <br/> <span className={styles.tagRole + ' ' + users[index].role === "User" ? styles.tagRoleAdmin : styles.tagRoleUser}>{users[index].role}</span> </p> : ""}

                  </div>
                </div>
              )
            })}
        </div>
      </Container>
      <div className={styles.lineGrade} ></div>
    </Jumbotron>
  );
}
