import React, { useState } from "react";
import { Jumbotron, Container} from "react-bootstrap";
import styles from "../CSS/Category.module.css";
import { useParams } from "react-router-dom";

export default function Category() {
  const { id } = useParams();

  const [category, setcCategory] = useState({
    "categoryId": "",
    "name": ""
  })

  useEffect( () => {
    if (id){
      fetch(`http://localhost:8080/categorys`)
        .then((response) => {
          return response.json()
        })
        .then(({name, brand, description, price, img}) => {
          setProcuct({
            name,
            brand,
            description,
            price,
            img
          })
        })
    }
  }, [id])

  return (
    <Jumbotron id="about" className={styles.Jumbotron} fluid>
      <Container>
        <div className={styles.card}>
          <h1 className={styles.heading}>
            <span className={styles.Us}>Category</span>
          </h1>
          <h2>{category.name} - {product.brand}</h2>
        </div>
      </Container>
      <div className={styles.lineGrade} ></div>
    </Jumbotron>
  );
}
