import React, { useEffect, useState, useRef } from "react";
import { Link, useParams } from "react-router-dom";
import { Jumbotron, Container, Image} from "react-bootstrap";
import styles from "../CSS/Posts.module.css";
import JoditEditor from "jodit-react";

export default function Posts() {
  const { id } = useParams();
  const [posts, setPosts] = useState([]);
  const [thread, setThread] = useState([]);
  const [users, setUsers] = useState([]);


  const editor = useRef(null)
	const [content, setContent] = useState('')

	const config = {
		readonly: false // all options from https://xdsoft.net/jodit/doc/
	}

  useEffect( () => {
    fetch(`http://localhost:8080/posts/${id}`)
    .then((response) => {
      return response.json()
    })
    .then((data) => {
      setPosts(data || [])
    }) 
    .catch ((error) =>{
        console.log("ERRRORRR")
    })

    fetch(`http://localhost:8080/threads/${id}`)
        .then((response) => {
          return response.json()
        })
        .then((data) => {
          setThread(data || [])
        })

    fetch(`http://localhost:8080/users/${id}`)
        .then((response) => {
          return response.json()
        })
        .then((data) => {
          setUsers(data || [])
        })

  }, [id])

  return (
    <Jumbotron id="about" className={styles.Jumbotron} fluid>
      <Container>
        <div className={styles.card}>
          <h1 className={styles.heading}>
            {thread.map((post) =>{
                return(
                  <span key={post.threadId} className={styles.Us}>{post.title}</span>
                )
            })}
          </h1>
            {thread.map((post, index) =>{
              return(
                <div key={post.threadId} className={styles.cardPost}>
                  <div className={styles.userInformation}>
                    <Image src={users[index] !== undefined ? users[index].photo : ""} alt="Account Image" className={styles.image}></Image>
                    {users[index] !== undefined ? <p className={styles.userInfo}>{users[index].avatarName} <br/> <span className={styles.tagRole + ' ' + styles[users[index].role]}>{users[index].role}</span> </p> : ""}
                  </div>
                  <div>
                    <p className={styles.postText}>{post.post}</p>
                  </div>
                </div>
              )
            })}

            {posts.length > 0 ? posts.map((post, index) =>{
              {index= index + 1}
              return(
                <div key={post.postId} className={styles.cardPost}>
                  <div className={styles.userInformation}>
                    <Image src={users[index] !== undefined ? users[index].photo : ""} alt="Account Image" className={styles.image}></Image>
                    {users[index] !== undefined ? <p className={styles.userInfo}>{users[index].avatarName} <br/> <span className={styles.tagRole + ' ' + styles[users[index].role]}>{users[index].role}</span> </p> : ""}
                  </div>
                  <div>
                    <p className={styles.postText}>{post.post}</p>
                  </div>
                  <div>
                  <a href = "#" className = {styles.Reply}>
                    &#8220;Reply
                  </a>
                  </div>
                </div>
              )
            }):console.log("DONT HAVE MORE POSTS")
            }

            <a href = "#" className = {styles.createButton}>
                + Post Reply
            </a>
        </div>
      </Container>
      <div className={styles.lineGrade} ></div>
    </Jumbotron>
  );
}
