import React, { useState } from "react";
import { Jumbotron, Container, Image, Alert } from "react-bootstrap";
import styles from "../CSS/Register.module.css";
import code from "../../commons/Assets/imagemLogo.png";

export default function AboutUs({ id }) {
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [avatarName, setAvatarName] = useState("");
  const [photo, setPhoto] = useState("");
  const [description, setDescription] = useState("");
  const [hasSubmissionSucceed, setHasSubmissionSucceed] = useState(false);
  const [error, setError] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const handleSubmit = async (evt) => {
      evt.preventDefault();

      try {
        const resp__raw = await fetch("http://localhost:8080/users", {
          origin: "*",
          method: "POST",
          body: JSON.stringify({
            username: `${name}`,
            password: `${password}`,
            email: `${email}`,
            avatarName: `${avatarName}`,
            photo: `${photo}`,
            description: `${description}`
          }),
          headers: {
            "Content-Type": "application/json",
          },
        });


        if(!resp__raw.ok){
          setError(true)
          setErrorMessage("Error Creating Account")
          //setErrorMessage(resp__raw.text().then(text => {console.log(JSON.parse(text).message)}))
          setHasSubmissionSucceed(false)
        } else{
          setHasSubmissionSucceed(true)
          setError(false)
        }
      } catch (err) {
        setError(true)
        setErrorMessage("Error Creating Account")
      } 
  }


  return (
    <Jumbotron id="about" className={styles.Jumbotron} fluid>
      <Container>
        <div className={styles.card}>
          <h1 className={styles.heading}>
            <span className={styles.Us}>Register</span>
            <Image src={code} alt="Logo" style={{ maxHeight: "36px", alignSelf: "center", marginLeft: "10px", paddingBottom: "4px"  }} />
          </h1>
              {hasSubmissionSucceed && (
                <Alert variant="success">
                  Successfully
                </Alert>
              )}

              {error && (
                <Alert variant="danger">{errorMessage}</Alert>
              )}
          <form onSubmit={handleSubmit} className={styles.form}>
            <label>Username:</label>
              <input
                type="text"
                value={name}
                onChange={e => setName(e.target.value)}
              />

            <label>Password:</label>
              <input
                type="password"
                value={password}
                onChange={e => setPassword(e.target.value)}
              />

            <label>Email:</label>
              <input
                type="email"
                value={email}
                onChange={e => setEmail(e.target.value)}
              />

            <label>
              Avatar Name:
              </label>
              <input
                type="text"
                value={avatarName}
                onChange={e => setAvatarName(e.target.value)}
              />

            <label>Photo:</label>
            <input
                type="text"
                value={photo}
                onChange={e => setPhoto(e.target.value)}
            />

            <label>Description:</label>
            <input
                type="text"
                value={description}
                onChange={e => setDescription(e.target.value)}
            />

            <input className={styles.buttonSubmit} type="submit" value="Submit" />
          </form>
        </div>
      </Container>
      <div className={styles.lineGrade} ></div>
    </Jumbotron>
  );
}
