                        const data = editor.getData();

                        setText(data)
                      } }
                      onFocus={ ( event, editor ) => {
                          const data = editor.getData();

                          if(data === "<p>Write your reply...</p>"){
                            editor.setData("");
                            setText("")
                          }
                      } }
              />
              <a href = "#" className = {styles.createButton} onClick={sendThread}>
                + Post
              </a>
            </div>
        </div> : <NotAccess/>
        }

      </Container>
      <div className={styles.lineGrade} ></div>
    </Jumbotron>
  );
}
