import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { Jumbotron, Container, Image} from "react-bootstrap";
import styles from "../CSS/Posts.module.css";

import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import NotAccess from "./NotAccess"

export default function Posts() {
  const { id } = useParams();
  const [posts, setPosts] = useState([]);
  const [thread, setThread] = useState([]);
  const [users, setUsers] = useState([]);
  const [text, setText] = useState("");


  const [editorDeTexto, setEditorDeTexto] = useState("");
  const [textToReply, setTextToReply] = useState("");


  var myHeaders = new Headers();
  myHeaders.append("Authorization", document.cookie.split("token=")[1]);

  var requestOptions = {
    method: 'GET',
    headers: myHeaders,
    redirect: 'follow'
  };

  useEffect( () => {
    fetch(`http://localhost:8080/posts/${id}`, requestOptions)
    .then((response) => {
      return response.json()
    })
    .then((data) => {
      setPosts(data || [])
    }) 
    .catch ((error) =>{
        console.log("ERRRORRR")
    })

    fetch(`http://localhost:8080/threads/${id}`, requestOptions)
        .then((response) => {
          return response.json()
        })
        .then((data) => {
          setThread(data || [])
        })

    fetch(`http://localhost:8080/users/${id}`, requestOptions)
        .then((response) => {
          return response.json()
        })
        .then((data) => {
          setUsers(data || [])
        })

  }, [id])

  function sendPost(e) {
    e.preventDefault();

    if(text !== ""){
      console.log(text);
    } else{
      alert("You can't post this");
    }
  }

  function quote(e){
    console.log(e.value);
    editorDeTexto.setData("OLAAA");
    setText("OLAAA")
  }

  return (
    <Jumbotron id="about" className={styles.Jumbotron} fluid>
      <Container>
        {document.cookie.split("token=")[1] ?
          <div className={styles.card}>
          <h1 className={styles.heading}>
            {thread.map((post) =>{
                return(
                  <span key={post.threadId} className={styles.Us}>{post.title}</span>
                )
            })}
          </h1>
            {thread.map((post, index) =>{
              return(
                <div key={post.threadId} className={styles.cardPost}>
                  <div className={styles.userInformation}>
                    <Image src={users[index] !== undefined ? users[index].photo : ""} alt="Account Image" className={styles.image}></Image>
                    {users[index] !== undefined ? <p className={styles.userInfo}> <Link className={styles.link} to={`/account/${users[index].userId}`}>{users[index].avatarName}</Link> <br/> <span className={styles.tagRole + ' ' + styles[users[index].role]}>{users[index].role}</span> </p> : ""}
                  </div>
                  <div>
                    <p className={styles.postText}>{post.post}</p>
                  </div>
                  <div>
                    <a href = "#reply" className = {styles.Reply} value="SIMMM" onClick={quote}>
                      &#8220;Reply
                    </a>
                  </div>
                </div>
              )
            })}

            {posts.length > 0 ? posts.map((post, index) =>{
              {index= index + 1}
              return(
                <div key={post.postId} className={styles.cardPost}>
                  <div className={styles.userInformation}>
                    <Image src={users[index] !== undefined ? users[index].photo : ""} alt="Account Image" className={styles.image}></Image>
                    {users[index] !== undefined ? <p className={styles.userInfo}><Link className={styles.link} to={`/account/${users[index].userId}`}>{users[index].avatarName}</Link> <br/> <span className={styles.tagRole + ' ' + styles[users[index].role]}>{users[index].role}</span> </p> : ""}
                  </div>
                  <div>
                    <p className={styles.postText}>{post.post}</p>
                  </div>
                  <div>
                    <a href = "#reply" className = {styles.Reply} onClick={quote}>
                      &#8220;Reply
                    </a>
                  </div>
                </div>
              )
            }):console.log("DONT HAVE MORE POSTS")
            }

            <a name="reply"></a>

            <div className = {styles.richtext}>
              <CKEditor
                      editor={ ClassicEditor }
                      data="<p>Write your reply...</p>"
                      onReady={ editor => {
                        setEditorDeTexto(editor);
                      }}
                      onChange={ ( event, editor ) => {
                        const data = editor.getData();

                        setText(data)
                      } }
                      onFocus={ ( event, editor ) => {
                          const data = editor.getData();

                          if(data === "<p>Write your reply...</p>"){
                            editor.setData("");
                            setText("")
                          }
                      } }
              />
              <a href = "#" className = {styles.createButton} onClick={sendPost}>
                + Post Reply
              </a>
            </div>
        </div> : <NotAccess/>
        }

      </Container>
      <div className={styles.lineGrade} ></div>
    </Jumbotron>
  );
}
