import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Jumbotron, Container, Image} from "react-bootstrap";
import styles from "../CSS/Account.module.css";

import NotAccess from "./NotAccess"


export default function Account () {
  const { id } = useParams();
  const [user, setUser] = useState([]);

  var myHeaders = new Headers();
  myHeaders.append("Authorization", document.cookie.split("token=")[1]);

  var requestOptions = {
    method: 'GET',
    headers: myHeaders,
    redirect: 'follow'
  };

  useEffect( () => {
    fetch(`http://localhost:8080/users/getuser/${id}`, requestOptions)
      .then((response) => {
        return response.json()
      })
      .then((data) => {
        setUser(data || [])
      })
  }, [id])

  return (
    <Jumbotron id="about" className={styles.Jumbotron} fluid>
      {document.cookie.split("token=")[1] ? 
      <Container>
        <div className={styles.card}>
          <h1 className={styles.heading}>
            <span className={styles.Us}>Account</span>
          </h1>
          <div className={styles.accountidentifiers}>
            <div key={user.id} className={styles.linkWithImage}>
              <Image src={user.photo} alt="Logo" style={{ maxHeight: "120px", maxWidth: "120px" , alignSelf: "center", marginLeft: "10px", paddingBottom: "4px", borderRadius: "50%", border: "1px solid white"  }}></Image>
              <div className={styles.identifiersText}>
                <div className={styles.emailAndAvatar}>
                <p><span >Email -</span><br/> {user.email}</p>
                <p>Avatar Name - <br/>{user.avatarName}</p>
                </div>
                <p>Description - <br/>{user.description}</p>
              </div>
            </div>
          </div>
        </div>
      </Container>
        : <NotAccess/> }
      <div className={styles.lineGrade} ></div>
    </Jumbotron>
  );
}
