import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Jumbotron, Container, Image} from "react-bootstrap";
import styles from "../CSS/Posts.module.css";

export default function Threads() {
  const { id } = useParams();
  const [category, setCategory] = useState("");
  const [posts, setPosts] = useState([]);

  useEffect( () => {
    fetch("http://localhost:8080/categorys/")
    .then((response) => {
      return response.json()
    })
    .then((data) => {
      setCategory(data[id - 1].name)
    })

    fetch(`http://localhost:8080/threads/${id}`)
      .then((response) => {
        return response.json()
      })
      .then((data) => {
        setPosts(data || [])
      })
  }, [id])


  return (
    <Jumbotron id="about" className={styles.Jumbotron} fluid>
      <Container>
        <div className={styles.card}>
          <h1 className={styles.heading}>
            {posts.map((post) =>{
                return(
                  <span className={styles.Us}>Threads {post.title}</span>
                )
            })}
          </h1>
          <div className={styles.links}>
            {posts.map((post) =>{
              return(
                <div key={post.threadId} className={styles.linkWithImage}>
                  <Image src="https://www.w3schools.com/howto/img_avatar.png" alt="Logo" style={{ maxHeight: "36px", alignSelf: "center", marginLeft: "10px", paddingBottom: "4px", borderRadius: "50%"  }}></Image>
                  <Link className={styles.categoryLink} to={`/post/${post.threadId}`} key={post.threadId}>{post.title} <p>By: {post.authorName}</p></Link>
                </div>
              )
            })}
          </div>
        </div>
      </Container>
      <div className={styles.lineGrade} ></div>
    </Jumbotron>
  );
}
